                alert(PMA_messages['strFormEmpty']);
                return false;
            }
        } else {
            // The execute_at field must not be empty for "once off" events
            $elm = this.$ajaxDialog.find('input[name=item_execute_at]');
            if ($elm.val() === '') {
                $elm.focus();
                alert(PMA_messages['strFormEmpty']);
                return false;
            }
        }
        return true;
    }
};

/**
